(function(){
	angular.module("Todolist",[])
	.controller("TodoAdd",function($scope){
		var parrafo=[{contenido:'Comprar nuevo sueter',id:'check1',check:false},
		{contenido:'Empezar fase de promoci√≥n',id:'check2',check:false},{contenido:'Leer un articulo',id:'check3',check:false},{contenido:'No intentar dormirse',id:'check4',check:false},{contenido:'Mirar "Sherlock"',id:'check5',check:false},{contenido:'Entrar QA para producto',id:'check6',check:false},
		{contenido:'Salir a trabajar',id:'check7',check:false}];
		$scope.parrafo=parrafo;
		$scope.Add=function(){
			
		    $scope.parrafo.push({contenido:$scope.todoInput,id:Math.random(),check:false});
			$scope.todoInput="";
			
		};
		$scope.eliminar=function(){
		     $scope.parrafo=$scope.parrafo.filter(function(item){
		     	return !item.check;
		     })
		 
		};
		
	});
})();